        public static FormSectionRow[] GetSectionRows(string section, string[] appSystemNames = null, UserTypeEnum? idenType = null)
        {
            var db = MongoFactory.GetFormSectionRowCollection();

            FormSectionRow[] rows = null;

            IMongoQueryable<FormSectionRow> query = db.AsQueryable().OrderBy(o => o.RowNumber);

            if (!string.IsNullOrEmpty(section))
            {
                query = query.Where(o => o.Section == section);
            }

            if (idenType != null)
            {
                rows = query
                    .Where(o => o.Controls.Where(p => p.IdentityTypes == null || p.IdentityTypes.Count() == 0 || p.IdentityTypes.Contains(idenType.Value)).Any())
                    .OrderBy(o => o.RowNumber).ToArray();


                foreach (var row in rows)
                {
                    if (row.Controls != null)
                    {
                        row.Controls = row.Controls
                            .Where(o => o.IdentityTypes == null || o.IdentityTypes.Count() == 0 || o.IdentityTypes.Contains(idenType.Value)).ToArray();
                    }
                }
            }
            else
            {
                rows = query.OrderBy(o => o.RowNumber).ToArray();
            }

            if (appSystemNames != null)
            {
                foreach (var row in rows)
                {
                    row.Controls = row.Controls.Where(o =>
                        (!o.HideOnSpecificApps || (o.HideOnSpecificApps && appSystemNames.Any(p => !o.HideAppSystemNames.Contains(p)))) && // o.HideAppSystemNames.Any(p => !appSystemNames.Contains(p))) && 
                        (!o.ShowOnSpecificApps || (o.ShowOnSpecificApps && o.AppSystemNames.Any(p => appSystemNames.Contains(p))))).ToArray();
                }
            }

            return rows;
        }

        #region Support Form Revision
        public int RevisionCode { get; set; } = 0;
        public string RevisionName { get; set; } = "Before Config Revision";

        public static FormSectionRow[] GetSectionRowsByRevision(string section, int revisionCode, string[] appSystemNames = null, UserTypeEnum? idenType = null)
        {
            // Use same logic as GetSections excepts that we look in FormSectionRevision instead.
            var db = MongoFactory.GetFormSectionRowRevisionCollection();
            FormSectionRow[] rows = null;

            IMongoQueryable<FormSectionRow> query = db.AsQueryable().Where(o => o.RevisionCode == revisionCode).OrderBy(o => o.RowNumber);

            if (!string.IsNullOrEmpty(section))
            {
                query = query.Where(o => o.Section == section);
            }

            if (idenType != null)
            {
                rows = query
                    .Where(o => o.Controls.Where(p => p.IdentityTypes == null || p.IdentityTypes.Count() == 0 || p.IdentityTypes.Contains(idenType.Value)).Any())
                    .OrderBy(o => o.RowNumber).ToArray();

                foreach (var row in rows)
                {
                    if (row.Controls != null)
                    {
                        row.Controls = row.Controls
                            .Where(o => o.IdentityTypes == null || o.IdentityTypes.Count() == 0 || o.IdentityTypes.Contains(idenType.Value)).ToArray();
                    }
                }
            }
            else
            {
                rows = query.OrderBy(o => o.RowNumber).ToArray();
            }

            if (appSystemNames != null)
            {
                foreach (var row in rows)
                {
                    row.Controls = row.Controls.Where(o =>
                        (!o.HideOnSpecificApps || (o.HideOnSpecificApps && appSystemNames.Any(p => !o.HideAppSystemNames.Contains(p)))) && // o.HideAppSystemNames.Any(p => !appSystemNames.Contains(p))) && 
                        (!o.ShowOnSpecificApps || (o.ShowOnSpecificApps && o.AppSystemNames.Any(p => appSystemNames.Contains(p))))).ToArray();
                }
            }

            return rows;
        }

        #endregion

        public string Section { get; set; }

        public int RowNumber { get; set; }

        public IEnumerable<FormControl> Controls { get; set; }

        #region Conditional display from other FormControl

        public FormControlDisplayCondition DisplayCondition { get; set; }

        #endregion
    }
